using UnityEngine;using System.Collections;public class Tile : MonoBehaviour {    public GameObject tileImage;    int rowPosition;    int colPosition;    public static int tileZLayer = 0;    private Tile below;    private Tile above;    private Tile left;    private Tile right;    enum TileType {ground,wall,pit};    //creates this tile, sets its position, connects it to adjacent tiles    public Tile(Tile belowTile, Tile leftTile, int rowPos, int colPos)    {        this.rowPosition = rowPos;        this.colPosition = colPos;        //force object to appear at specified position        //givenImage.transform.position.Set(rowPos, colPos, tileZLayer);    }    //initializes this Tile relative to the tile below and left of it    //both tiles will be "pointing" (not using pointers) to each other    public void Initialize(Tile belowTile, Tile leftTile)    {        this.below = belowTile;        this.left = leftTile;        if (belowTile != null)            belowTile.above = this;        if (leftTile != null)            leftTile.right = this;    }}